template <typename hashvaluetype> hashvaluetype maskfnc(int bits) {
  assert(bits > 0);
  assert(bits <= sizeof(hashvaluetype) * 8);
  hashvaluetype x = static_cast<hashvaluetype>(1) << (bits - 1);
  return x ^ (x - 1);
}

